mapper.select:
SELECT o.*, od.id AS od_id, od.number AS od_number, od.amount AS od_amount,
od.name, od.image, od.order_id, od.dish_id, od.setmeal_id, od.dish_flavor

FROM (
    SELECT * FROM orders


            AND status = #{status}


    ORDER BY order_time DESC
    LIMIT #{offset}, #{limit}
) o
LEFT JOIN order_detail od ON o.id = od.order_id

SELECT o.*, GROUP_CONCAT(od.name SEPARATOR ', ') AS orderDishes FROM orders o LEFT JOIN order_detail od on o.id = od.order_id


        AND o.status = #{status}


        AND o.number = #{number}


        AND o.phone = #{phone}


        AND o.order_time >= #{beginTime}


        AND o.order_time <= #{endTime}


GROUP BY o.id, o.order_time
ORDER BY o.order_time DESC
